import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';
import { CloudArrowUpIcon, DocumentTextIcon, ExclamationCircleIcon } from '@heroicons/react/24/outline';
import { useTheme } from '../contexts/ThemeContext';

// Register ChartJS components
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const DashboardPage = () => {
  const [results, setResults] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [stats, setStats] = useState({
    totalScans: 0,
    scansWithNodules: 0,
    totalNodules: 0,
    averageNodulesPerScan: 0,
    failedScans: 0
  });
  const { darkMode } = useTheme();

  useEffect(() => {
    const fetchResults = async () => {
      try {
        setIsLoading(true);
        const response = await axios.get('/api/results');
        const data = response.data || [];
        setResults(data);
        
        if (data.length === 0) {
          // No results to process
          setStats({
            totalScans: 0,
            scansWithNodules: 0,
            totalNodules: 0,
            averageNodulesPerScan: 0,
            failedScans: 0
          });
          setIsLoading(false);
          return;
        }
        
        // Calculate statistics
        const scansWithNodules = await Promise.all(
          data.map(async (result) => {
            try {
              const detailsResponse = await axios.get(`/api/results/${result.case_name}/details`);
              const details = detailsResponse.data?.details || '';
              // Simple text parsing to determine if nodules were found
              const hasNodules = details && 
                !details.includes('No nodules detected') && 
                !details.includes('No detailed information available');
              
              // Count nodules by looking for "Nodule X:" patterns in the text
              const noduleMatches = details.match(/Nodule \d+:/g) || [];
              const noduleCount = noduleMatches.length;
              
              return {
                ...result,
                hasNodules,
                noduleCount,
                hasDetails: !!details
              };
            } catch (error) {
              console.error(`Error fetching details for ${result.case_name}:`, error);
              // Return the result with default values even if details fail to load
              return { 
                ...result, 
                hasNodules: false, 
                noduleCount: 0,
                hasDetails: false,
                error: true 
              };
            }
          })
        );
        
        // Filter out results that had errors if needed
        const validResults = scansWithNodules.filter(scan => !scan.error);
        const validResultsCount = validResults.length;
        
        // Continue even if some scans had errors
        const withNodules = scansWithNodules.filter(scan => scan.hasNodules);
        const totalNodules = scansWithNodules.reduce((sum, scan) => sum + scan.noduleCount, 0);
        const failedScans = scansWithNodules.filter(scan => scan.error).length;
        
        setStats({
          totalScans: data.length,
          scansWithNodules: withNodules.length,
          totalNodules: totalNodules,
          averageNodulesPerScan: validResultsCount > 0 ? (totalNodules / validResultsCount).toFixed(2) : 0,
          failedScans: failedScans
        });
        
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching results:', error);
        
        // Provide more specific error message based on the error
        let errorMessage = 'Failed to load results data. Please try refreshing the page.';
        if (error.response) {
          // The request was made and the server responded with a status code
          // that falls out of the range of 2xx
          console.error('Error response data:', error.response.data);
          errorMessage = `Server error: ${error.response.status}. Please try again later.`;
        } else if (error.request) {
          // The request was made but no response was received
          errorMessage = 'No response from server. Please check your network connection.';
        }
        
        setError(errorMessage);
        
        // Still set some empty results to allow partial dashboard rendering
        setResults([]);
        setStats({
          totalScans: 0,
          scansWithNodules: 0,
          totalNodules: 0,
          averageNodulesPerScan: 0,
          failedScans: 0
        });
        
        setIsLoading(false);
      }
    };

    fetchResults();
  }, []);

  // Prepare data for the chart
  const chartData = {
    labels: ['With Nodules', 'Without Nodules'],
    datasets: [
      {
        label: 'CT Scans',
        data: [stats.scansWithNodules, stats.totalScans - stats.scansWithNodules],
        backgroundColor: ['rgba(79, 70, 229, 0.6)', 'rgba(209, 213, 219, 0.6)'],
        borderColor: ['rgba(79, 70, 229, 1)', 'rgba(209, 213, 219, 1)'],
        borderWidth: 1,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
        labels: {
          color: darkMode ? '#fff' : '#000',
        }
      },
      title: {
        display: true,
        text: 'Nodule Detection Results',
        color: darkMode ? '#fff' : '#000',
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        ticks: {
          precision: 0,
          color: darkMode ? '#fff' : '#000',
        },
        grid: {
          color: darkMode ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.1)',
        }
      },
      x: {
        ticks: {
          color: darkMode ? '#fff' : '#000',
        },
        grid: {
          color: darkMode ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.1)',
        }
      }
    },
  };

  return (
    <div className={`space-y-6 ${darkMode ? 'text-white' : ''}`}>
      <div className="flex justify-between items-center">
        <h1 className={`text-2xl font-semibold ${darkMode ? 'text-white' : 'text-gray-900'}`}>Dashboard</h1>
        <Link to="/upload" className="btn-primary">
          Upload New Scan
        </Link>
      </div>

      {isLoading ? (
        <div className="text-center py-12">
          <div className="inline-block h-8 w-8 animate-spin rounded-full border-4 border-solid border-indigo-600 border-r-transparent"></div>
          <p className={`mt-4 ${darkMode ? 'text-gray-300' : 'text-gray-600'}`}>Loading dashboard data...</p>
        </div>
      ) : error ? (
        <div className={`${darkMode ? 'bg-red-900' : 'bg-red-50'} p-4 rounded-md`}>
          <div className="flex">
            <ExclamationCircleIcon className={`h-5 w-5 ${darkMode ? 'text-red-300' : 'text-red-400'}`} aria-hidden="true" />
            <div className="ml-3">
              <h3 className={`text-sm font-medium ${darkMode ? 'text-red-100' : 'text-red-800'}`}>{error}</h3>
            </div>
          </div>
        </div>
      ) : (
        <>
          {/* Stats Cards */}
          <div className="grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-4">
            <div className={`${darkMode ? 'bg-gray-800' : 'bg-white'} overflow-hidden shadow rounded-lg`}>
              <div className="px-4 py-5 sm:p-6">
                <div className="flex items-center">
                  <div className="flex-shrink-0 bg-indigo-500 rounded-md p-3">
                    <DocumentTextIcon className="h-6 w-6 text-white" aria-hidden="true" />
                  </div>
                  <div className="ml-5 w-0 flex-1">
                    <dl>
                      <dt className={`text-sm font-medium ${darkMode ? 'text-gray-300' : 'text-gray-500'} truncate`}>Total Scans</dt>
                      <dd className={`text-3xl font-semibold ${darkMode ? 'text-white' : 'text-gray-900'}`}>{stats.totalScans}</dd>
                    </dl>
                  </div>
                </div>
              </div>
            </div>

            <div className={`${darkMode ? 'bg-gray-800' : 'bg-white'} overflow-hidden shadow rounded-lg`}>
              <div className="px-4 py-5 sm:p-6">
                <div className="flex items-center">
                  <div className="flex-shrink-0 bg-green-500 rounded-md p-3">
                    <DocumentTextIcon className="h-6 w-6 text-white" aria-hidden="true" />
                  </div>
                  <div className="ml-5 w-0 flex-1">
                    <dl>
                      <dt className={`text-sm font-medium ${darkMode ? 'text-gray-300' : 'text-gray-500'} truncate`}>Scans With Nodules</dt>
                      <dd className={`text-3xl font-semibold ${darkMode ? 'text-white' : 'text-gray-900'}`}>{stats.scansWithNodules}</dd>
                    </dl>
                  </div>
                </div>
              </div>
            </div>

            <div className={`${darkMode ? 'bg-gray-800' : 'bg-white'} overflow-hidden shadow rounded-lg`}>
              <div className="px-4 py-5 sm:p-6">
                <div className="flex items-center">
                  <div className="flex-shrink-0 bg-yellow-500 rounded-md p-3">
                    <DocumentTextIcon className="h-6 w-6 text-white" aria-hidden="true" />
                  </div>
                  <div className="ml-5 w-0 flex-1">
                    <dl>
                      <dt className={`text-sm font-medium ${darkMode ? 'text-gray-300' : 'text-gray-500'} truncate`}>Total Nodules</dt>
                      <dd className={`text-3xl font-semibold ${darkMode ? 'text-white' : 'text-gray-900'}`}>{stats.totalNodules}</dd>
                    </dl>
                  </div>
                </div>
              </div>
            </div>

            <div className={`${darkMode ? 'bg-gray-800' : 'bg-white'} overflow-hidden shadow rounded-lg`}>
              <div className="px-4 py-5 sm:p-6">
                <div className="flex items-center">
                  <div className="flex-shrink-0 bg-blue-500 rounded-md p-3">
                    <DocumentTextIcon className="h-6 w-6 text-white" aria-hidden="true" />
                  </div>
                  <div className="ml-5 w-0 flex-1">
                    <dl>
                      <dt className={`text-sm font-medium ${darkMode ? 'text-gray-300' : 'text-gray-500'} truncate`}>Avg. Nodules/Scan</dt>
                      <dd className={`text-3xl font-semibold ${darkMode ? 'text-white' : 'text-gray-900'}`}>{stats.averageNodulesPerScan}</dd>
                    </dl>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Chart */}
          <div className={`${darkMode ? 'bg-gray-800' : 'bg-white'} p-6 shadow rounded-lg`}>
            <h2 className={`text-lg font-medium ${darkMode ? 'text-white' : 'text-gray-900'} mb-4`}>Results Distribution</h2>
            <div className="h-64">
              <Bar data={chartData} options={chartOptions} />
            </div>
          </div>

          {/* Recent Results */}
          <div className={`${darkMode ? 'bg-gray-800' : 'bg-white'} shadow rounded-lg`}>
            <div className={`px-4 py-5 border-b ${darkMode ? 'border-gray-700' : 'border-gray-200'} sm:px-6`}>
              <h3 className={`text-lg font-medium ${darkMode ? 'text-white' : 'text-gray-900'}`}>Recent Results</h3>
            </div>
            <ul className={`divide-y ${darkMode ? 'divide-gray-700' : 'divide-gray-200'}`}>
              {results.slice(0, 5).map((result) => (
                <li key={result.case_name}>
                  <Link to={`/results/${result.case_name}`} className={`block ${darkMode ? 'hover:bg-gray-700' : 'hover:bg-gray-50'}`}>
                    <div className="px-4 py-4 sm:px-6">
                      <div className="flex items-center justify-between">
                        <div className="flex items-center">
                          <div className="flex-shrink-0">
                            <DocumentTextIcon className={`h-6 w-6 ${darkMode ? 'text-gray-400' : 'text-gray-400'}`} aria-hidden="true" />
                          </div>
                          <div className="ml-3">
                            <p className="text-sm font-medium text-indigo-600 truncate">{result.case_name}</p>
                            <p className={`text-sm ${darkMode ? 'text-gray-400' : 'text-gray-500'}`}>
                              Processed on {new Date(result.timestamp).toLocaleString()}
                            </p>
                          </div>
                        </div>
                        <div className="ml-2 flex-shrink-0 flex">
                          <p className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-green-100 text-green-800">
                            View Details
                          </p>
                        </div>
                      </div>
                    </div>
                  </Link>
                </li>
              ))}
              {results.length === 0 && (
                <li className={`px-4 py-5 sm:px-6 text-center ${darkMode ? 'text-gray-400' : 'text-gray-500'}`}>
                  No results available. Upload a CT scan to get started.
                </li>
              )}
            </ul>
            {results.length > 5 && (
              <div className={`px-4 py-3 ${darkMode ? 'bg-gray-700' : 'bg-gray-50'} text-right sm:px-6`}>
                <Link 
                  to="/results" 
                  className="text-sm font-medium text-indigo-600 hover:text-indigo-500"
                >
                  View all results
                </Link>
              </div>
            )}
          </div>
        </>
      )}
    </div>
  );
};

export default DashboardPage;
