import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';
import { CloudArrowUpIcon, DocumentTextIcon, ExclamationCircleIcon } from '@heroicons/react/24/outline';

// Register ChartJS components
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const DashboardPage = () => {
  const [results, setResults] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [stats, setStats] = useState({
    totalScans: 0,
    scansWithNodules: 0,
    totalNodules: 0,
    averageNodulesPerScan: 0,
  });

  useEffect(() => {
    const fetchResults = async () => {
      try {
        setIsLoading(true);
        const response = await axios.get('/api/results');
        const data = response.data;
        setResults(data);
        
        // Calculate statistics
        const scansWithNodules = await Promise.all(
          data.map(async (result) => {
            try {
              const detailsResponse = await axios.get(`/api/results/${result.case_name}/details`);
              const details = detailsResponse.data.details;
              // Simple text parsing to determine if nodules were found
              return {
                ...result,
                hasNodules: !details.includes('No nodules detected'),
                noduleCount: (details.match(/Nodule \d+:/g) || []).length
              };
            } catch (error) {
              console.error(`Error fetching details for ${result.case_name}:`, error);
              return { ...result, hasNodules: false, noduleCount: 0 };
            }
          })
        );
        
        const withNodules = scansWithNodules.filter(scan => scan.hasNodules);
        const totalNodules = scansWithNodules.reduce((sum, scan) => sum + scan.noduleCount, 0);
        
        setStats({
          totalScans: data.length,
          scansWithNodules: withNodules.length,
          totalNodules: totalNodules,
          averageNodulesPerScan: data.length > 0 ? (totalNodules / data.length).toFixed(2) : 0,
        });
        
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching results:', error);
        setError('Failed to load results data');
        setIsLoading(false);
      }
    };

    fetchResults();
  }, []);

  // Prepare data for the chart
  const chartData = {
    labels: ['With Nodules', 'Without Nodules'],
    datasets: [
      {
        label: 'CT Scans',
        data: [stats.scansWithNodules, stats.totalScans - stats.scansWithNodules],
        backgroundColor: ['rgba(79, 70, 229, 0.6)', 'rgba(209, 213, 219, 0.6)'],
        borderColor: ['rgba(79, 70, 229, 1)', 'rgba(209, 213, 219, 1)'],
        borderWidth: 1,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Nodule Detection Results',
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        ticks: {
          precision: 0,
        },
      },
    },
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h1 className="text-2xl font-semibold text-gray-900">Dashboard</h1>
        <Link to="/upload" className="btn-primary">
          Upload New Scan
        </Link>
      </div>

      {isLoading ? (
        <div className="text-center py-12">
          <div className="inline-block h-8 w-8 animate-spin rounded-full border-4 border-solid border-indigo-600 border-r-transparent"></div>
          <p className="mt-4 text-gray-600">Loading dashboard data...</p>
        </div>
      ) : error ? (
        <div className="bg-red-50 p-4 rounded-md">
          <div className="flex">
            <ExclamationCircleIcon className="h-5 w-5 text-red-400" aria-hidden="true" />
            <div className="ml-3">
              <h3 className="text-sm font-medium text-red-800">{error}</h3>
            </div>
          </div>
        </div>
      ) : (
        <>
          {/* Stats Cards */}
          <div className="grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-4">
            <div className="bg-white overflow-hidden shadow rounded-lg">
              <div className="px-4 py-5 sm:p-6">
                <div className="flex items-center">
                  <div className="flex-shrink-0 bg-indigo-500 rounded-md p-3">
                    <DocumentTextIcon className="h-6 w-6 text-white" aria-hidden="true" />
                  </div>
                  <div className="ml-5 w-0 flex-1">
                    <dl>
                      <dt className="text-sm font-medium text-gray-500 truncate">Total Scans</dt>
                      <dd className="text-3xl font-semibold text-gray-900">{stats.totalScans}</dd>
                    </dl>
                  </div>
                </div>
              </div>
            </div>

            <div className="bg-white overflow-hidden shadow rounded-lg">
              <div className="px-4 py-5 sm:p-6">
                <div className="flex items-center">
                  <div className="flex-shrink-0 bg-green-500 rounded-md p-3">
                    <DocumentTextIcon className="h-6 w-6 text-white" aria-hidden="true" />
                  </div>
                  <div className="ml-5 w-0 flex-1">
                    <dl>
                      <dt className="text-sm font-medium text-gray-500 truncate">Scans With Nodules</dt>
                      <dd className="text-3xl font-semibold text-gray-900">{stats.scansWithNodules}</dd>
                    </dl>
                  </div>
                </div>
              </div>
            </div>

            <div className="bg-white overflow-hidden shadow rounded-lg">
              <div className="px-4 py-5 sm:p-6">
                <div className="flex items-center">
                  <div className="flex-shrink-0 bg-yellow-500 rounded-md p-3">
                    <DocumentTextIcon className="h-6 w-6 text-white" aria-hidden="true" />
                  </div>
                  <div className="ml-5 w-0 flex-1">
                    <dl>
                      <dt className="text-sm font-medium text-gray-500 truncate">Total Nodules</dt>
                      <dd className="text-3xl font-semibold text-gray-900">{stats.totalNodules}</dd>
                    </dl>
                  </div>
                </div>
              </div>
            </div>

            <div className="bg-white overflow-hidden shadow rounded-lg">
              <div className="px-4 py-5 sm:p-6">
                <div className="flex items-center">
                  <div className="flex-shrink-0 bg-blue-500 rounded-md p-3">
                    <DocumentTextIcon className="h-6 w-6 text-white" aria-hidden="true" />
                  </div>
                  <div className="ml-5 w-0 flex-1">
                    <dl>
                      <dt className="text-sm font-medium text-gray-500 truncate">Avg. Nodules/Scan</dt>
                      <dd className="text-3xl font-semibold text-gray-900">{stats.averageNodulesPerScan}</dd>
                    </dl>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Chart */}
          <div className="bg-white p-6 shadow rounded-lg">
            <h2 className="text-lg font-medium text-gray-900 mb-4">Results Distribution</h2>
            <div className="h-64">
              <Bar data={chartData} options={chartOptions} />
            </div>
          </div>

          {/* Recent Results */}
          <div className="bg-white shadow rounded-lg">
            <div className="px-4 py-5 border-b border-gray-200 sm:px-6">
              <h3 className="text-lg font-medium text-gray-900">Recent Results</h3>
            </div>
            <ul className="divide-y divide-gray-200">
              {results.slice(0, 5).map((result) => (
                <li key={result.case_name}>
                  <Link to={`/results/${result.case_name}`} className="block hover:bg-gray-50">
                    <div className="px-4 py-4 sm:px-6">
                      <div className="flex items-center justify-between">
                        <div className="flex items-center">
                          <div className="flex-shrink-0">
                            <DocumentTextIcon className="h-6 w-6 text-gray-400" aria-hidden="true" />
                          </div>
                          <div className="ml-3">
                            <p className="text-sm font-medium text-indigo-600 truncate">{result.case_name}</p>
                            <p className="text-sm text-gray-500">
                              Processed on {new Date(result.timestamp).toLocaleString()}
                            </p>
                          </div>
                        </div>
                        <div className="ml-2 flex-shrink-0 flex">
                          <p className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-green-100 text-green-800">
                            View Details
                          </p>
                        </div>
                      </div>
                    </div>
                  </Link>
                </li>
              ))}
              {results.length === 0 && (
                <li className="px-4 py-5 sm:px-6 text-center text-gray-500">
                  No results available. Upload a CT scan to get started.
                </li>
              )}
            </ul>
            {results.length > 5 && (
              <div className="px-4 py-3 bg-gray-50 text-right sm:px-6">
                <Link 
                  to="/results" 
                  className="text-sm font-medium text-indigo-600 hover:text-indigo-500"
                >
                  View all results
                </Link>
              </div>
            )}
          </div>
        </>
      )}
    </div>
  );
};

export default DashboardPage;
