import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import { ExclamationCircleIcon, ArrowLeftIcon, EyeIcon, ViewfinderCircleIcon } from '@heroicons/react/24/outline';
import InteractiveViewer from '../components/InteractiveViewer';

const ResultDetailPage = () => {
  const { caseId } = useParams();
  const [result, setResult] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [viewMode, setViewMode] = useState('static'); // 'static' or 'interactive'

  useEffect(() => {
    const fetchResultDetails = async () => {
      try {
        setIsLoading(true);
        const response = await axios.get(`/api/results/${caseId}`);
        setResult(response.data);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching result details:', error);
        setError('Failed to load result details. Please try again later.');
        setIsLoading(false);
      }
    };

    fetchResultDetails();
  }, [caseId]);

  if (isLoading) {
    return (
      <div className="text-center py-12">
        <div className="inline-block h-8 w-8 animate-spin rounded-full border-4 border-solid border-indigo-600 border-r-transparent"></div>
        <p className="mt-4 text-gray-600">Loading result details...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 p-4 rounded-md">
        <div className="flex">
          <ExclamationCircleIcon className="h-5 w-5 text-red-400" aria-hidden="true" />
          <div className="ml-3">
            <h3 className="text-sm font-medium text-red-800">{error}</h3>
          </div>
        </div>
      </div>
    );
  }

  if (!result) {
    return (
      <div className="text-center py-12">
        <p className="text-gray-600">No result found for this case ID.</p>
        <Link to="/results" className="mt-4 inline-block text-indigo-600 hover:text-indigo-500">
          Back to all results
        </Link>
      </div>
    );
  }

  // Format the timestamp
  const formattedDate = new Date(result.timestamp).toLocaleString();

  // Parse details if available
  const hasNodules = result.details && !result.details.includes('No nodules detected');
  
  // Extract nodule information if available
  const nodules = [];
  if (hasNodules && result.details) {
    const lines = result.details.split('\n');
    let currentNodule = null;
    
    for (const line of lines) {
      if (line.startsWith('Nodule ')) {
        if (currentNodule) {
          nodules.push(currentNodule);
        }
        currentNodule = {
          id: line.replace(':', '').trim(),
          coordinates: '',
          radius: '',
          confidence: '',
          merged: ''
        };
      } else if (currentNodule) {
        if (line.includes('Coordinates')) {
          currentNodule.coordinates = line.split(':')[1].trim();
        } else if (line.includes('Radius')) {
          currentNodule.radius = line.split(':')[1].trim();
        } else if (line.includes('Confidence')) {
          currentNodule.confidence = line.split(':')[1].trim();
        } else if (line.includes('Merged from')) {
          currentNodule.merged = line.split(':')[1].trim();
        }
      }
    }
    
    // Add the last nodule
    if (currentNodule) {
      nodules.push(currentNodule);
    }
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div className="flex items-center space-x-3">
          <Link to="/results" className="text-gray-500 hover:text-gray-700">
            <ArrowLeftIcon className="h-5 w-5" />
          </Link>
          <h1 className="text-2xl font-semibold text-gray-900">
            Result: {result.case_name}
          </h1>
        </div>
        <span className="text-sm text-gray-500">
          Processed on {formattedDate}
        </span>
      </div>

      {/* Visualization mode toggle */}
      <div className="bg-white shadow rounded-lg overflow-hidden">
        <div className="px-4 py-3 border-b border-gray-200 sm:px-6 flex justify-between items-center">
          <h3 className="text-lg font-medium text-gray-900">Visualization</h3>
          
          <div className="flex items-center space-x-2 border rounded-lg overflow-hidden">
            <button
              onClick={() => setViewMode('static')}
              className={`px-3 py-1.5 text-sm font-medium flex items-center ${
                viewMode === 'static' 
                  ? 'bg-indigo-100 text-indigo-700' 
                  : 'bg-white text-gray-700 hover:bg-gray-50'
              }`}
            >
              <EyeIcon className="h-4 w-4 mr-1" />
              Static View
            </button>
            <button
              onClick={() => setViewMode('interactive')}
              className={`px-3 py-1.5 text-sm font-medium flex items-center ${
                viewMode === 'interactive' 
                  ? 'bg-indigo-100 text-indigo-700' 
                  : 'bg-white text-gray-700 hover:bg-gray-50'
              }`}
            >
              <ViewfinderCircleIcon className="h-4 w-4 mr-1" />
              Interactive
            </button>
          </div>
        </div>

        {viewMode === 'static' ? (
          <div className="p-6 flex justify-center">
            <img 
              src={result.image_url} 
              alt={`CT scan results for ${result.case_name}`} 
              className="max-w-full h-auto rounded shadow-lg"
            />
          </div>
        ) : (
          <InteractiveViewer caseId={caseId} />
        )}
      </div>

      {/* Result details */}
      <div className="bg-white shadow rounded-lg overflow-hidden">
        <div className="px-4 py-5 border-b border-gray-200 sm:px-6">
          <h3 className="text-lg font-medium text-gray-900">Details</h3>
        </div>
        <div className="p-6">
          {hasNodules ? (
            <div>
              <div className="mb-4">
                <h4 className="text-md font-medium text-gray-900">Summary</h4>
                <p className="text-gray-700">
                  {nodules.length} nodules detected in this scan.
                </p>
              </div>
              
              <h4 className="text-md font-medium text-gray-900 mb-2">Detected Nodules</h4>
              <div className="overflow-x-auto">
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Nodule
                      </th>
                      <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Coordinates (z,y,x)
                      </th>
                      <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Radius (mm)
                      </th>
                      <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Confidence
                      </th>
                      <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Merged From
                      </th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {nodules.map((nodule, index) => (
                      <tr key={index} className={index % 2 === 0 ? 'bg-white' : 'bg-gray-50'}>
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                          {nodule.id}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          {nodule.coordinates}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          {nodule.radius}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          {nodule.confidence}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          {nodule.merged || 'N/A'}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          ) : (
            <div className="text-gray-700">
              <p className="mb-4">No nodules were detected in this scan.</p>
              <p>The processing was completed successfully, but no lung nodules meeting the confidence threshold were identified.</p>
            </div>
          )}
          
          <div className="mt-8">
            <h4 className="text-md font-medium text-gray-900 mb-2">Raw Results</h4>
            <pre className="bg-gray-50 p-4 rounded-md text-sm text-gray-700 overflow-x-auto max-h-96 overflow-y-auto">
              {result.details || 'No detailed results available.'}
            </pre>
          </div>
        </div>
      </div>

      {/* Actions */}
      <div className="flex justify-end space-x-3">
        <Link to="/upload" className="btn-primary">
          Upload New Scan
        </Link>
        <Link to="/results" className="btn-secondary">
          View All Results
        </Link>
      </div>
    </div>
  );
};

export default ResultDetailPage;
