import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import axios from 'axios';
import { DocumentTextIcon, ExclamationCircleIcon, TrashIcon, XMarkIcon } from '@heroicons/react/24/outline';
import { useAuth } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';
import { ROLES } from '../contexts/AuthContext';
import PlaceholderImage from '../components/PlaceholderImage';

const ResultsPage = () => {
  const location = useLocation();
  const [results, setResults] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [scanToDelete, setScanToDelete] = useState(null);
  const [deleteSuccess, setDeleteSuccess] = useState(location.state?.deleteSuccess || '');
  const { user } = useAuth();
  const { darkMode } = useTheme();

  // Clear the location state after displaying a success message
  useEffect(() => {
    if (location.state?.deleteSuccess) {
      // Clear the success message after 3 seconds
      const timer = setTimeout(() => {
        setDeleteSuccess('');
        // Clear the state from the location to avoid showing the message on refresh
        window.history.replaceState({}, document.title);
      }, 3000);
      
      return () => clearTimeout(timer);
    }
  }, [location.state]);

  const fetchResults = async () => {
    try {
      setIsLoading(true);
      // Use the user-specific endpoint for doctors and the global endpoint for admins/superadmins
      const endpoint = (user.role === ROLES.ADMIN || user.role === ROLES.SUPERADMIN) 
        ? '/api/results' 
        : '/api/results/user';
      const response = await axios.get(endpoint);
      
      // Sort by timestamp, newest first
      const sortedResults = response.data.sort((a, b) => 
        new Date(b.timestamp) - new Date(a.timestamp)
      );
      
      // Debug log to see what data we're getting
      console.log('Results data:', sortedResults);
      
      setResults(sortedResults);
      setIsLoading(false);
    } catch (error) {
      console.error('Error fetching results:', error);
      setError('Failed to load results. Please try again later.');
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchResults();
  }, [user.role]);

  // Check if the current user can delete a specific scan
  const canDeleteScan = (scan) => {
    // Extract owner username from case name (if the format is username_filename)
    const scanOwner = scan.case_name.split('_')[0];
    
    // Superadmin can delete any scan
    if (user.role === ROLES.SUPERADMIN) {
      return true;
    }
    
    // Owners can always delete their own scans
    if (scanOwner === user.username) {
      return true;
    }
    
    // Admins can delete scans from doctors they've created
    if (user.role === ROLES.ADMIN) {
      // In a real implementation, this would check if the admin created the doctor
      // For now, we'll assume admins can delete any doctor's scans
      return true;
    }
    
    // By default, users can't delete other users' scans
    return false;
  };

  // Opens the delete confirmation modal
  const openDeleteModal = (scan, e) => {
    e.preventDefault(); // Prevent navigation to scan details
    e.stopPropagation(); // Prevent event bubbling
    setScanToDelete(scan);
    setShowDeleteModal(true);
  };

  // Handles scan deletion
  const handleDeleteScan = async () => {
    try {
      await axios.delete(`/api/results/${scanToDelete.case_name}`);
      setResults(results.filter(r => r.case_name !== scanToDelete.case_name));
      setDeleteSuccess(`Scan ${scanToDelete.case_name} has been deleted successfully.`);
      setScanToDelete(null);
      setShowDeleteModal(false);
      
      // Clear success message after 3 seconds
      setTimeout(() => {
        setDeleteSuccess('');
      }, 3000);
    } catch (error) {
      console.error('Error deleting scan:', error);
      setError('Failed to delete scan. Please try again later.');
      setShowDeleteModal(false);
    }
  };

  // Helper function to extract a title from scan details
  const extractTitle = (scan) => {
    // Check if there's patient information available
    if (scan.patient_info && scan.patient_info.description) {
      // Limit the length of the description to avoid too long titles
      const description = scan.patient_info.description;
      return description.length > 100 ? description.substring(0, 100) + '...' : description;
    }
    
    // Fall back to extracting from details if no patient info description
    if (!scan.details) return "No details available";
    
    // Look for patient information in the details
    const lines = scan.details.split('\n');
    
    // Look for Clinical Notes section
    let foundClinicNotes = false;
    for (let i = 0; i < lines.length; i++) {
      if (lines[i].includes('Clinical Notes:')) {
        foundClinicNotes = true;
        // Return the clinical notes (on the next line or after the colon)
        if (lines[i].split('Clinical Notes:').length > 1) {
          const notes = lines[i].split('Clinical Notes:')[1].trim();
          // Limit length of long notes
          return notes.length > 100 ? notes.substring(0, 100) + '...' : notes || "No clinical notes available";
        } else if (i + 1 < lines.length) {
          const notes = lines[i + 1].trim();
          return notes.length > 100 ? notes.substring(0, 100) + '...' : notes || "No clinical notes available";
        }
      }
    }
    
    // If no clinical notes found, extract the first meaningful line
    const titleLine = lines.find(line => 
      !line.startsWith('Nodule') && 
      !line.includes('Coordinates') && 
      !line.includes('Radius') && 
      !line.includes('Confidence') &&
      !line.includes('segmentation performed')
    );
    
    if (titleLine) {
      return titleLine.length > 100 ? titleLine.substring(0, 100) + '...' : titleLine;
    }
    
    return scan.details.length > 100 ? scan.details.substring(0, 100) + '...' : scan.details;
  };

  // Helper function to extract patient name
  const extractPatientName = (scan) => {
    // Default username to use if no patient name is found
    const defaultName = scan.case_name.split('_')[0];
    
    // First check patient_info object if available
    if (scan.patient_info && scan.patient_info.name) {
      return scan.patient_info.name;
    }
    
    // Fall back to extracting from details
    if (!scan.details) return defaultName;
    
    const lines = scan.details.split('\n');
    
    // Look for Name: or Patient Name: in the details
    for (let i = 0; i < lines.length; i++) {
      if (lines[i].includes('Name:') || lines[i].includes('Patient Name:')) {
        // Extract name from current line if it's after the colon
        if (lines[i].split(':').length > 1) {
          const name = lines[i].split(':')[1].trim();
          return name || defaultName;
        } 
        // Otherwise check the next line which might contain the name
        else if (i + 1 < lines.length) {
          return lines[i + 1].trim() || defaultName;
        }
      }
    }
    
    // Default to the scan owner username if no patient name found
    return defaultName;
  };

  if (isLoading) {
    return (
      <div className={`text-center py-12 ${darkMode ? 'text-white' : ''}`}>
        <div className="inline-block h-8 w-8 animate-spin rounded-full border-4 border-solid border-indigo-600 border-r-transparent"></div>
        <p className={`mt-4 ${darkMode ? 'text-gray-300' : 'text-gray-600'}`}>Loading results...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className={`${darkMode ? 'bg-red-900' : 'bg-red-50'} p-4 rounded-md`}>
        <div className="flex">
          <ExclamationCircleIcon className={`h-5 w-5 ${darkMode ? 'text-red-300' : 'text-red-400'}`} aria-hidden="true" />
          <div className="ml-3">
            <h3 className={`text-sm font-medium ${darkMode ? 'text-red-300' : 'text-red-800'}`}>{error}</h3>
          </div>
        </div>
      </div>
    );
  }

  if (results.length === 0) {
    return (
      <div className={`space-y-6 ${darkMode ? 'text-white' : ''}`}>
        <div className="flex justify-between items-center">
          <h1 className={`text-2xl font-semibold ${darkMode ? 'text-white' : 'text-gray-900'}`}>
            {user.role === ROLES.DOCTOR ? 'My Results' : 'All Results'}
          </h1>
          <Link 
            to="/upload" 
            className={`px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500`}
          >
            Upload New Scan
          </Link>
        </div>
        
        <div className={`${darkMode ? 'bg-gray-800' : 'bg-white'} shadow rounded-lg p-12 text-center`}>
          <DocumentTextIcon className={`mx-auto h-12 w-12 ${darkMode ? 'text-gray-500' : 'text-gray-400'}`} />
          <h3 className={`mt-2 text-lg font-medium ${darkMode ? 'text-white' : 'text-gray-900'}`}>No results found</h3>
          <p className={`mt-1 ${darkMode ? 'text-gray-300' : 'text-gray-500'}`}>Upload a CT scan to get started.</p>
          <div className="mt-6">
            <Link 
              to="/upload"
              className="inline-flex items-center px-4 py-2 border border-transparent shadow-sm text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
            >
              Upload CT Scan
            </Link>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className={`space-y-6 ${darkMode ? 'text-white' : ''}`}>
      {deleteSuccess && (
        <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded relative" role="alert">
          <span className="block sm:inline">{deleteSuccess}</span>
        </div>
      )}
      
      <div className="flex justify-between items-center">
        <div>
          <h1 className={`text-2xl font-semibold ${darkMode ? 'text-white' : 'text-gray-900'}`}>
            {user.role === ROLES.DOCTOR ? 'My Results' : 'All Results'}
          </h1>
          <p className={`text-sm ${darkMode ? 'text-gray-300' : 'text-gray-500'} mt-1`}>
            {user.role === ROLES.DOCTOR 
              ? 'You can view and manage the CT scan results you have uploaded.' 
              : 'As an administrator, you can view and manage CT scan results from all users.'}
          </p>
        </div>
        <Link 
          to="/upload" 
          className="px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
        >
          Upload New Scan
        </Link>
      </div>

      {/* Blog-style grid layout for scan results */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {results.map((result) => {
          // Extract owner username from case name (if the format is username_filename)
          const scanOwner = result.case_name.split('_')[0];
          const isOwn = scanOwner === user.username;
          const canDelete = canDeleteScan(result);
          const scanTitle = extractTitle(result);
          const patientName = extractPatientName(result);
          const formattedDate = new Date(result.timestamp).toLocaleString();
          
          // Create direct image URLs
          const token = localStorage.getItem('token');
          const imageUrl = `/api/results/${result.case_name}/image?token=${token}`;
          
          return (
            <div 
              key={result.case_name} 
              className={`${darkMode ? 'bg-gray-800' : 'bg-white'} rounded-lg shadow overflow-hidden flex flex-col h-full`}
            >
              {/* Scan Image */}
              <Link to={`/results/${result.case_name}`} className="block overflow-hidden h-48 relative">
                <img 
                  src={imageUrl} 
                  alt={`Scan ${result.case_name}`} 
                  className="w-full h-full object-cover transition-transform duration-300 hover:scale-105"
                  onError={(e) => {
                    console.log(`Image failed to load for ${result.case_name}`);
                    // Just hide the image on error
                    e.target.style.display = 'none';
                  }}
                />
                
                {/* Status indicator overlay */}
                <div className="absolute top-2 right-2">
                  {!isOwn && (
                    <span className={`px-2 py-0.5 text-xs font-semibold rounded-full ${
                      darkMode 
                        ? 'bg-purple-900 text-purple-100' 
                        : 'bg-purple-100 text-purple-800'
                    }`}>
                      Dr. {scanOwner}
                    </span>
                  )}
                  {isOwn && (
                    <span className={`px-2 py-0.5 text-xs font-semibold rounded-full ${
                      darkMode 
                        ? 'bg-blue-900 text-blue-100' 
                        : 'bg-blue-100 text-blue-800'
                    }`}>
                      Your Scan
                    </span>
                  )}
                </div>
              </Link>
              
              {/* Content */}
              <div className="p-4 flex-grow flex flex-col">
                <Link to={`/results/${result.case_name}`} className="block">
                  <h3 className={`text-lg font-semibold mb-2 ${darkMode ? 'text-white' : 'text-gray-900'} hover:text-indigo-600`}>
                    {scanTitle || 'No clinical notes available'}
                  </h3>
                </Link>
                
                <div className={`flex-grow ${result.patient_info ? 'mb-2' : ''}`}></div>
                
                <div className={`flex items-center mt-auto pt-3 ${darkMode ? 'border-gray-700' : 'border-gray-200'} border-t`}>
                  <div className={`p-2 rounded-md mr-2 ${darkMode ? 'bg-gray-700' : 'bg-gray-100'}`}>
                    <DocumentTextIcon className={`h-5 w-5 ${darkMode ? 'text-gray-300' : 'text-gray-600'}`} />
                  </div>
                  <div className="flex-grow">
                    <p className={`text-sm font-medium ${darkMode ? 'text-gray-300' : 'text-gray-900'}`}>
                      <span className="font-semibold">Patient:</span> {patientName}
                    </p>
                    {result.patient_info && result.patient_info.age && (
                      <p className={`text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                        <span className="font-semibold">Age:</span> {result.patient_info.age}
                      </p>
                    )}
                    <p className={`text-xs mt-1 ${darkMode ? 'text-gray-400' : 'text-gray-500'}`}>
                      {formattedDate}
                    </p>
                  </div>
                  
                  {canDelete && (
                    <button
                      onClick={(e) => openDeleteModal(result, e)}
                      className={`p-1.5 rounded-full ${
                        darkMode 
                          ? 'text-gray-400 hover:bg-gray-700 hover:text-red-300' 
                          : 'text-gray-400 hover:bg-gray-100 hover:text-red-600'
                      }`}
                      title="Delete scan"
                    >
                      <TrashIcon className="h-5 w-5" />
                    </button>
                  )}
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {/* Delete confirmation modal */}
      {showDeleteModal && (
        <div className="fixed inset-0 z-10 overflow-y-auto" aria-labelledby="modal-title" role="dialog" aria-modal="true">
          <div className="flex items-end justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
            <div className="fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity" aria-hidden="true" onClick={() => setShowDeleteModal(false)}></div>
            <span className="hidden sm:inline-block sm:align-middle sm:h-screen" aria-hidden="true">&#8203;</span>
            <div className={`inline-block align-bottom ${darkMode ? 'bg-gray-800' : 'bg-white'} rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full`}>
              <div className={`${darkMode ? 'bg-gray-800' : 'bg-white'} px-4 pt-5 pb-4 sm:p-6 sm:pb-4`}>
                <div className="sm:flex sm:items-start">
                  <div className="mx-auto flex-shrink-0 flex items-center justify-center h-12 w-12 rounded-full bg-red-100 sm:mx-0 sm:h-10 sm:w-10">
                    <ExclamationCircleIcon className="h-6 w-6 text-red-600" aria-hidden="true" />
                  </div>
                  <div className="mt-3 text-center sm:mt-0 sm:ml-4 sm:text-left">
                    <h3 className={`text-lg leading-6 font-medium ${darkMode ? 'text-white' : 'text-gray-900'}`} id="modal-title">
                      Delete Scan
                    </h3>
                    <div className="mt-2">
                      <p className={`text-sm ${darkMode ? 'text-gray-300' : 'text-gray-500'}`}>
                        Are you sure you want to delete the scan "{scanToDelete?.case_name}"? This action cannot be undone.
                      </p>
                    </div>
                  </div>
                </div>
              </div>
              <div className={`${darkMode ? 'bg-gray-800' : 'bg-gray-50'} px-4 py-3 sm:px-6 sm:flex sm:flex-row-reverse`}>
                <button
                  type="button"
                  className="w-full inline-flex justify-center rounded-md border border-transparent shadow-sm px-4 py-2 bg-red-600 text-base font-medium text-white hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500 sm:ml-3 sm:w-auto sm:text-sm"
                  onClick={handleDeleteScan}
                >
                  Delete
                </button>
                <button
                  type="button"
                  className={`mt-3 w-full inline-flex justify-center rounded-md border ${darkMode ? 'border-gray-600 bg-gray-700 text-gray-300 hover:bg-gray-600' : 'border-gray-300 bg-white text-gray-700 hover:bg-gray-50'} shadow-sm px-4 py-2 text-base font-medium focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 sm:mt-0 sm:ml-3 sm:w-auto sm:text-sm`}
                  onClick={() => setShowDeleteModal(false)}
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ResultsPage;
