import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';
import { PlusIcon, PencilIcon, TrashIcon, ExclamationCircleIcon } from '@heroicons/react/24/outline';

const UsersPage = () => {
  const [users, setUsers] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showCreateUserModal, setShowCreateUserModal] = useState(false);
  const [showEditUserModal, setShowEditUserModal] = useState(false);
  const [showDeleteUserModal, setShowDeleteUserModal] = useState(false);
  const [userToEdit, setUserToEdit] = useState(null);
  const [userToDelete, setUserToDelete] = useState(null);
  
  const { user } = useAuth();
  
  // Form states for creating and editing users
  const [formUsername, setFormUsername] = useState('');
  const [formPassword, setFormPassword] = useState('');
  const [formRole, setFormRole] = useState('doctor');
  const [formError, setFormError] = useState('');
  
  // Fetch users from the API
  const fetchUsers = async () => {
    try {
      setIsLoading(true);
      const response = await axios.get('/api/users');
      setUsers(response.data);
      setIsLoading(false);
    } catch (error) {
      console.error('Error fetching users:', error);
      setError('Failed to load users. Please try again later.');
      setIsLoading(false);
    }
  };
  
  useEffect(() => {
    fetchUsers();
  }, []);
  
  // Check if current user is an admin
  if (user.role !== 'admin') {
    return (
      <div className="text-center py-12">
        <div className="bg-red-50 p-4 rounded-md inline-block">
          <div className="flex">
            <ExclamationCircleIcon className="h-5 w-5 text-red-400" />
            <div className="ml-3">
              <h3 className="text-sm font-medium text-red-800">Unauthorized Access</h3>
              <div className="mt-2 text-sm text-red-700">
                <p>You do not have permission to access this page. This page is only available to administrators.</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
  
  // Create a new user
  const handleCreateUser = async () => {
    try {
      setFormError('');
      if (!formUsername || !formPassword) {
        setFormError('Username and password are required.');
        return;
      }
      
      const response = await axios.post('/api/users', {
        username: formUsername,
        password: formPassword,
        role: formRole
      });
      
      // Reset form and close modal
      setFormUsername('');
      setFormPassword('');
      setFormRole('doctor');
      setShowCreateUserModal(false);
      
      // Refresh users list
      fetchUsers();
    } catch (error) {
      console.error('Error creating user:', error);
      setFormError(error.response?.data?.error || 'Failed to create user. Please try again.');
    }
  };
  
  // Update an existing user
  const handleUpdateUser = async () => {
    try {
      setFormError('');
      
      const dataToUpdate = {};
      if (formPassword) {
        dataToUpdate.password = formPassword;
      }
      if (formRole) {
        dataToUpdate.role = formRole;
      }
      
      if (Object.keys(dataToUpdate).length === 0) {
        setFormError('Please make at least one change.');
        return;
      }
      
      const response = await axios.put(`/api/users/${userToEdit.username}`, dataToUpdate);
      
      // Reset form and close modal
      setFormPassword('');
      setFormRole(userToEdit.role);
      setUserToEdit(null);
      setShowEditUserModal(false);
      
      // Refresh users list
      fetchUsers();
    } catch (error) {
      console.error('Error updating user:', error);
      setFormError(error.response?.data?.error || 'Failed to update user. Please try again.');
    }
  };
  
  // Delete a user
  const handleDeleteUser = async () => {
    try {
      const response = await axios.delete(`/api/users/${userToDelete.username}`);
      
      // Reset and close modal
      setUserToDelete(null);
      setShowDeleteUserModal(false);
      
      // Refresh users list
      fetchUsers();
    } catch (error) {
      console.error('Error deleting user:', error);
      setError(error.response?.data?.error || 'Failed to delete user. Please try again.');
    }
  };
  
  // Open the edit user modal
  const openEditUserModal = (user) => {
    setUserToEdit(user);
    setFormUsername(user.username);
    setFormPassword(''); // Don't prefill password for security
    setFormRole(user.role);
    setFormError('');
    setShowEditUserModal(true);
  };
  
  // Open the delete user modal
  const openDeleteUserModal = (user) => {
    setUserToDelete(user);
    setShowDeleteUserModal(true);
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h1 className="text-2xl font-semibold text-gray-900">User Management</h1>
        <button 
          onClick={() => {
            setFormUsername('');
            setFormPassword('');
            setFormRole('doctor');
            setFormError('');
            setShowCreateUserModal(true);
          }}
          className="btn-primary flex items-center"
        >
          <PlusIcon className="h-5 w-5 mr-1" />
          Add User
        </button>
      </div>

      {error && (
        <div className="bg-red-50 p-4 rounded-md">
          <div className="flex">
            <ExclamationCircleIcon className="h-5 w-5 text-red-400" />
            <div className="ml-3">
              <h3 className="text-sm font-medium text-red-800">{error}</h3>
            </div>
          </div>
        </div>
      )}

      {isLoading ? (
        <div className="text-center py-12">
          <div className="inline-block h-8 w-8 animate-spin rounded-full border-4 border-solid border-indigo-600 border-r-transparent"></div>
          <p className="mt-4 text-gray-600">Loading users...</p>
        </div>
      ) : (
        <div className="bg-white shadow overflow-hidden sm:rounded-md">
          <ul className="divide-y divide-gray-200">
            {users.map((user) => (
              <li key={user.username}>
                <div className="px-4 py-4 flex items-center sm:px-6">
                  <div className="min-w-0 flex-1 sm:flex sm:items-center sm:justify-between">
                    <div>
                      <div className="flex text-sm">
                        <p className="font-medium text-indigo-600 truncate">{user.username}</p>
                        <p className="ml-1 flex-shrink-0 font-normal text-gray-500">
                          ({user.role})
                        </p>
                      </div>
                    </div>
                  </div>
                  <div className="ml-5 flex-shrink-0 flex space-x-2">
                    <button
                      type="button"
                      onClick={() => openEditUserModal(user)}
                      className="inline-flex items-center px-3 py-1.5 border border-transparent text-xs font-medium rounded text-indigo-700 bg-indigo-100 hover:bg-indigo-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                    >
                      <PencilIcon className="h-4 w-4 mr-1" />
                      Edit
                    </button>
                    <button
                      type="button"
                      onClick={() => openDeleteUserModal(user)}
                      className="inline-flex items-center px-3 py-1.5 border border-transparent text-xs font-medium rounded text-red-700 bg-red-100 hover:bg-red-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
                    >
                      <TrashIcon className="h-4 w-4 mr-1" />
                      Delete
                    </button>
                  </div>
                </div>
              </li>
            ))}
            {users.length === 0 && (
              <li>
                <div className="px-4 py-8 text-center text-gray-500">
                  No users found. Click "Add User" to create a new user.
                </div>
              </li>
            )}
          </ul>
        </div>
      )}

      {/* Create User Modal */}
      {showCreateUserModal && (
        <div className="fixed inset-0 bg-gray-500 bg-opacity-75 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 max-w-md w-full">
            <h2 className="text-xl font-semibold mb-4">Create New User</h2>
            
            {formError && (
              <div className="mb-4 bg-red-50 p-3 rounded-md">
                <p className="text-sm text-red-700">{formError}</p>
              </div>
            )}
            
            <div className="space-y-4">
              <div>
                <label htmlFor="username" className="block text-sm font-medium text-gray-700">
                  Username
                </label>
                <input
                  type="text"
                  id="username"
                  name="username"
                  value={formUsername}
                  onChange={(e) => setFormUsername(e.target.value)}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                />
              </div>
              
              <div>
                <label htmlFor="password" className="block text-sm font-medium text-gray-700">
                  Password
                </label>
                <input
                  type="password"
                  id="password"
                  name="password"
                  value={formPassword}
                  onChange={(e) => setFormPassword(e.target.value)}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                />
              </div>
              
              <div>
                <label htmlFor="role" className="block text-sm font-medium text-gray-700">
                  Role
                </label>
                <select
                  id="role"
                  name="role"
                  value={formRole}
                  onChange={(e) => setFormRole(e.target.value)}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                >
                  <option value="doctor">Doctor</option>
                  <option value="admin">Admin</option>
                </select>
              </div>
            </div>
            
            <div className="mt-6 flex justify-end space-x-3">
              <button
                type="button"
                className="btn-secondary"
                onClick={() => setShowCreateUserModal(false)}
              >
                Cancel
              </button>
              <button
                type="button"
                className="btn-primary"
                onClick={handleCreateUser}
              >
                Create User
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Edit User Modal */}
      {showEditUserModal && userToEdit && (
        <div className="fixed inset-0 bg-gray-500 bg-opacity-75 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 max-w-md w-full">
            <h2 className="text-xl font-semibold mb-4">Edit User: {userToEdit.username}</h2>
            
            {formError && (
              <div className="mb-4 bg-red-50 p-3 rounded-md">
                <p className="text-sm text-red-700">{formError}</p>
              </div>
            )}
            
            <div className="space-y-4">
              <div>
                <label htmlFor="edit-password" className="block text-sm font-medium text-gray-700">
                  New Password (leave blank to keep current)
                </label>
                <input
                  type="password"
                  id="edit-password"
                  name="password"
                  value={formPassword}
                  onChange={(e) => setFormPassword(e.target.value)}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                />
              </div>
              
              <div>
                <label htmlFor="edit-role" className="block text-sm font-medium text-gray-700">
                  Role
                </label>
                <select
                  id="edit-role"
                  name="role"
                  value={formRole}
                  onChange={(e) => setFormRole(e.target.value)}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                >
                  <option value="doctor">Doctor</option>
                  <option value="admin">Admin</option>
                </select>
              </div>
            </div>
            
            <div className="mt-6 flex justify-end space-x-3">
              <button
                type="button"
                className="btn-secondary"
                onClick={() => {
                  setUserToEdit(null);
                  setShowEditUserModal(false);
                }}
              >
                Cancel
              </button>
              <button
                type="button"
                className="btn-primary"
                onClick={handleUpdateUser}
              >
                Update User
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Delete User Confirmation Modal */}
      {showDeleteUserModal && userToDelete && (
        <div className="fixed inset-0 bg-gray-500 bg-opacity-75 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 max-w-md w-full">
            <h2 className="text-xl font-semibold mb-4">Delete User</h2>
            
            <p className="text-gray-700 mb-4">
              Are you sure you want to delete the user "{userToDelete.username}"? 
              This action cannot be undone.
            </p>
            
            <div className="mt-6 flex justify-end space-x-3">
              <button
                type="button"
                className="btn-secondary"
                onClick={() => {
                  setUserToDelete(null);
                  setShowDeleteUserModal(false);
                }}
              >
                Cancel
              </button>
              <button
                type="button"
                className="btn-danger"
                onClick={handleDeleteUser}
              >
                Delete
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default UsersPage;
