import React from 'react';
import { DocumentTextIcon } from '@heroicons/react/24/outline';
import { useTheme } from '../contexts/ThemeContext';

/**
 * A component that displays a placeholder for scan images that fail to load
 */
const PlaceholderImage = ({ className = 'h-full w-full', text = 'Image not available' }) => {
  const { darkMode } = useTheme();
  
  return (
    <div 
      className={`${className} flex flex-col items-center justify-center ${darkMode ? 'bg-gray-800' : 'bg-gray-100'}`}
    >
      <DocumentTextIcon 
        className={`h-12 w-12 ${darkMode ? 'text-gray-500' : 'text-gray-400'} mb-2`} 
        aria-hidden="true" 
      />
      <p className={`text-xs text-center ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
        {text}
      </p>
    </div>
  );
};

export default PlaceholderImage;
