import React, { useState } from 'react';
import { Outlet, Link, useLocation, useNavigate } from 'react-router-dom';
import { useAuth, ROLES, canAccessAdminArea } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';
import {
  HomeIcon,
  CloudArrowUpIcon,
  DocumentTextIcon,
  Bars3Icon,
  XMarkIcon,
  ArrowRightOnRectangleIcon,
  UserCircleIcon,
  UserGroupIcon,
  BeakerIcon,
  Cog6ToothIcon,
  ShieldCheckIcon
} from '@heroicons/react/24/outline';

const Layout = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const { user, logout } = useAuth();
  const { darkMode } = useTheme();
  const location = useLocation();
  const navigate = useNavigate();

  // Base navigation for all users
  const baseNavigation = [
    { name: 'Dashboard', href: '/', icon: HomeIcon },
    { name: 'Upload', href: '/upload', icon: CloudArrowUpIcon },
    { name: 'Results', href: '/results', icon: DocumentTextIcon },
    { name: 'Settings', href: '/settings', icon: Cog6ToothIcon },
  ];

  // Admin-only navigation items
  const adminNavigation = [
    { name: 'Users', href: '/users', icon: UserGroupIcon }
  ];

  // Determine which navigation items to show based on user role
  const navigation = canAccessAdminArea(user?.role)
    ? [...baseNavigation, ...adminNavigation]
    : baseNavigation;

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  // Function to get role badge component
  const getRoleBadge = (role) => {
    switch(role) {
      case ROLES.SUPERADMIN:
        return (
          <span className={`ml-1 px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${darkMode ? 'bg-purple-900 text-purple-100' : 'bg-purple-100 text-purple-800'}`}>
            <ShieldCheckIcon className="mr-1 h-3 w-3" />
            Super Admin
          </span>
        );
      case ROLES.ADMIN:
        return (
          <span className={`ml-1 px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${darkMode ? 'bg-blue-900 text-blue-100' : 'bg-blue-100 text-blue-800'}`}>
            <ShieldCheckIcon className="mr-1 h-3 w-3" />
            Admin
          </span>
        );
      default:
        return null;
    }
  };

  // Get user display name
  const getUserDisplayName = () => {
    if (user?.firstName && user?.lastName) {
      return `${user.firstName} ${user.lastName}`;
    } else if (user?.firstName) {
      return user.firstName;
    } else {
      return user?.username || 'User';
    }
  };

  return (
    <div className={`min-h-screen ${darkMode ? 'bg-gray-900 text-white' : 'bg-gray-100'} flex`}>
      {/* Mobile sidebar overlay */}
      {sidebarOpen && (
        <div 
          className="fixed inset-0 z-40 bg-gray-600 bg-opacity-75 md:hidden"
          onClick={() => setSidebarOpen(false)} 
        />
      )}

      {/* Sidebar */}
      <div className={`fixed inset-y-0 left-0 z-50 w-64 ${darkMode ? 'bg-gray-800' : 'bg-white'} transform transition-transform duration-300 ease-in-out md:relative md:translate-x-0 ${sidebarOpen ? 'translate-x-0' : '-translate-x-full'}`}>
        <div className="h-full flex flex-col">
          {/* Sidebar header with logo and close button */}
          <div className={`flex items-center justify-between h-16 px-4 border-b ${darkMode ? 'border-gray-700' : 'border-gray-200'}`}>
            <Link to="/" className="flex items-center">
              <div className="h-8 w-8 rounded-full bg-indigo-600 flex items-center justify-center text-white">
                <BeakerIcon className="h-5 w-5" />
              </div>
              <span className={`ml-2 text-xl font-semibold ${darkMode ? 'text-white' : 'text-gray-900'}`}>PulmoScan</span>
            </Link>
            <button
              type="button"
              className={`md:hidden ${darkMode ? 'text-gray-400 hover:text-gray-300' : 'text-gray-500 hover:text-gray-600'}`}
              onClick={() => setSidebarOpen(false)}
            >
              <XMarkIcon className="h-5 w-5" />
            </button>
          </div>

          {/* Navigation links */}
          <nav className="flex-1 px-2 py-4 space-y-1 overflow-y-auto">
            {navigation.map((item) => {
              const isActive = location.pathname === item.href;
              return (
                <Link
                  key={item.name}
                  to={item.href}
                  className={`group flex items-center px-2 py-2 text-sm font-medium rounded-md ${
                    isActive
                      ? darkMode 
                        ? 'bg-indigo-800 text-white' 
                        : 'bg-indigo-50 text-indigo-600'
                      : darkMode 
                        ? 'text-gray-300 hover:bg-gray-700 hover:text-white' 
                        : 'text-gray-600 hover:bg-gray-50 hover:text-gray-900'
                  }`}
                >
                  <item.icon className={`mr-3 h-5 w-5 ${
                    isActive 
                      ? darkMode 
                        ? 'text-indigo-300' 
                        : 'text-indigo-600' 
                      : darkMode 
                        ? 'text-gray-400 group-hover:text-gray-300' 
                        : 'text-gray-400 group-hover:text-gray-500'
                  }`} />
                  {item.name}
                </Link>
              );
            })}
          </nav>

          {/* User info and logout */}
          <div className={`border-t ${darkMode ? 'border-gray-700' : 'border-gray-200'} p-4`}>
            <div className="flex items-center">
              <UserCircleIcon className={`h-6 w-6 ${darkMode ? 'text-gray-300' : 'text-gray-400'}`} />
              <div className="ml-3">
                <div className="flex items-center">
                  <p className={`text-sm font-medium ${darkMode ? 'text-white' : 'text-gray-700'}`}>{user?.username || 'User'}</p>
                  {user?.role && getRoleBadge(user.role)}
                </div>
              </div>
            </div>
            <button
              onClick={handleLogout}
              className={`mt-3 w-full flex items-center justify-center px-4 py-2 border rounded-md shadow-sm text-sm font-medium ${
                darkMode 
                  ? 'border-gray-600 text-gray-200 bg-gray-700 hover:bg-gray-600' 
                  : 'border-gray-300 text-gray-700 bg-white hover:bg-gray-50'
              }`}
            >
              <ArrowRightOnRectangleIcon className={`h-4 w-4 mr-2 ${darkMode ? 'text-gray-300' : 'text-gray-400'}`} />
              Sign out
            </button>
          </div>
        </div>
      </div>

      {/* Main content area */}
      <div className="flex-1 flex flex-col">
        {/* Mobile header */}
        <div className={`md:hidden sticky top-0 z-10 flex items-center justify-between h-16 ${darkMode ? 'bg-gray-800 shadow-md' : 'bg-white shadow-sm'} px-4`}>
          <button
            type="button"
            className={`${darkMode ? 'text-gray-400 hover:text-gray-300' : 'text-gray-500 hover:text-gray-600'} focus:outline-none focus:ring-2 focus:ring-inset focus:ring-indigo-500`}
            onClick={() => setSidebarOpen(true)}
          >
            <Bars3Icon className="h-5 w-5" />
          </button>
          <Link to="/" className="flex items-center">
            <div className="h-8 w-8 rounded-full bg-indigo-600 flex items-center justify-center text-white">
              <BeakerIcon className="h-5 w-5" />
            </div>
            <span className={`ml-2 text-lg font-medium ${darkMode ? 'text-white' : 'text-gray-900'}`}>PulmoScan</span>
          </Link>
          <div className="flex items-center">
            <UserCircleIcon className={`h-5 w-5 ${darkMode ? 'text-gray-300' : 'text-gray-500'}`} />
          </div>
        </div>

        {/* Desktop header (only visible on md breakpoint and up) */}
        <div className={`hidden md:flex md:justify-end sticky top-0 z-10 ${darkMode ? 'bg-gray-900' : 'bg-gray-100'} p-4`}>
          <div className={`flex items-center px-3 py-1.5 rounded-full ${darkMode ? 'bg-gray-800' : 'bg-white'} shadow-sm`}>
            <UserCircleIcon className={`h-5 w-5 ${darkMode ? 'text-gray-300' : 'text-gray-500'} mr-2`} />
            <span className={`text-sm ${darkMode ? 'text-gray-300' : 'text-gray-600'} mr-1`}>
              Welcome,
            </span>
            <span className={`font-medium text-sm ${darkMode ? 'text-white' : 'text-gray-900'}`}>
              {getUserDisplayName()}
            </span>
            {user?.role && (
              <span className={`ml-2 px-2 py-0.5 text-xs font-semibold rounded-full ${
                darkMode 
                  ? 'bg-indigo-900 text-indigo-100' 
                  : 'bg-indigo-100 text-indigo-800'
              }`}>
                {user.role}
              </span>
            )}
          </div>
        </div>

        {/* Page content */}
        <main className="flex-1 p-6">
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default Layout;
