import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useDropzone } from 'react-dropzone';
import { CloudArrowUpIcon, ExclamationCircleIcon } from '@heroicons/react/24/outline';

const UploadPage = () => {
  const [files, setFiles] = useState([]);
  const [rawFile, setRawFile] = useState(null);
  const [confidence, setConfidence] = useState(0.5);
  const [lungsOnly, setLungsOnly] = useState(false);
  const [noSegmentation, setNoSegmentation] = useState(false);
  const [isUploading, setIsUploading] = useState(false);
  const [error, setError] = useState(null);
  const [progress, setProgress] = useState(0);
  const navigate = useNavigate();

  // Configure dropzone for CT scan files
  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    accept: {
      'application/octet-stream': ['.mhd', '.raw', '.nii.gz', '.nii'],
      'application/dicom': ['.dcm'],
    },
    multiple: false,
    onDrop: (acceptedFiles) => {
      // First check if we're dealing with an MHD file
      const mhdFile = acceptedFiles.find(file => file.name.endsWith('.mhd'));
      
      if (mhdFile) {
        setFiles([mhdFile]);
        // Let the user know they need to upload the associated RAW file
        setError("You've uploaded an MHD file. Please also upload the associated RAW file.");
      } else {
        setFiles(acceptedFiles);
        setError(null);
      }
    },
  });

  // Configure dropzone for RAW files (only needed when an MHD file is selected)
  const { getRootProps: getRawRootProps, getInputProps: getRawInputProps, isDragActive: isRawDragActive } = useDropzone({
    accept: {
      'application/octet-stream': ['.raw'],
    },
    multiple: false,
    onDrop: (acceptedFiles) => {
      if (acceptedFiles.length > 0) {
        setRawFile(acceptedFiles[0]);
        setError(null);
      }
    },
  });

  const handleUpload = async () => {
    if (files.length === 0) {
      setError('Please select a file to upload');
      return;
    }

    // If user selected an MHD file but no RAW file, show error
    if (files[0].name.endsWith('.mhd') && !rawFile) {
      setError('Please upload the associated RAW file for your MHD file');
      return;
    }

    setIsUploading(true);
    setProgress(0);
    setError(null);

    const formData = new FormData();
    formData.append('file', files[0]);
    
    // Add RAW file if present
    if (rawFile) {
      formData.append('raw_file', rawFile);
    }
    
    // Add processing options
    formData.append('confidence', confidence);
    formData.append('lungs_only', lungsOnly);
    formData.append('no_segmentation', noSegmentation);

    try {
      const response = await axios.post('/api/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
        onUploadProgress: (progressEvent) => {
          const percentCompleted = Math.round((progressEvent.loaded * 100) / progressEvent.total);
          setProgress(percentCompleted);
        },
      });

      if (response.data.success) {
        // Navigate to results page for this scan
        navigate(`/results/${response.data.job_id}`);
      } else {
        setError(response.data.error || 'Unknown error occurred');
        setIsUploading(false);
      }
    } catch (error) {
      console.error('Upload error:', error);
      setError(error.response?.data?.error || 'Error uploading file. Please try again.');
      setIsUploading(false);
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h1 className="text-2xl font-semibold text-gray-900">Upload CT Scan</h1>
      </div>

      <div className="bg-white shadow rounded-lg p-6">
        <div className="space-y-6">
          {/* Main file dropzone */}
          <div>
            <label className="form-label">CT Scan File (MHD, NIFTI, DICOM)</label>
            <div
              {...getRootProps()}
              className={`mt-1 flex justify-center px-6 pt-5 pb-6 border-2 border-dashed rounded-md cursor-pointer ${
                isDragActive ? 'border-indigo-500 bg-indigo-50' : 'border-gray-300'
              }`}
            >
              <div className="space-y-1 text-center">
                <CloudArrowUpIcon className="mx-auto h-12 w-12 text-gray-400" />
                <div className="flex text-sm text-gray-600">
                  <input id="file-upload" {...getInputProps()} />
                  <p className="pl-1">
                    Drag and drop your CT scan file here, or click to select file
                  </p>
                </div>
                <p className="text-xs text-gray-500">MHD, NIFTI (.nii.gz, .nii), or DICOM files</p>
              </div>
            </div>
            {files.length > 0 && (
              <div className="mt-2">
                <p className="text-sm text-gray-500">
                  Selected file: <span className="font-medium text-indigo-600">{files[0].name}</span> ({(files[0].size / (1024 * 1024)).toFixed(2)} MB)
                </p>
              </div>
            )}
          </div>

          {/* RAW file dropzone (only shown when MHD file is selected) */}
          {files.length > 0 && files[0].name.endsWith('.mhd') && (
            <div>
              <label className="form-label">RAW File (required for MHD format)</label>
              <div
                {...getRawRootProps()}
                className={`mt-1 flex justify-center px-6 pt-5 pb-6 border-2 border-dashed rounded-md cursor-pointer ${
                  isRawDragActive ? 'border-indigo-500 bg-indigo-50' : 'border-gray-300'
                }`}
              >
                <div className="space-y-1 text-center">
                  <CloudArrowUpIcon className="mx-auto h-12 w-12 text-gray-400" />
                  <div className="flex text-sm text-gray-600">
                    <input id="raw-file-upload" {...getRawInputProps()} />
                    <p className="pl-1">
                      Drag and drop the associated RAW file here, or click to select file
                    </p>
                  </div>
                  <p className="text-xs text-gray-500">Required for MHD format</p>
                </div>
              </div>
              {rawFile && (
                <div className="mt-2">
                  <p className="text-sm text-gray-500">
                    Selected RAW file: <span className="font-medium text-indigo-600">{rawFile.name}</span> ({(rawFile.size / (1024 * 1024)).toFixed(2)} MB)
                  </p>
                </div>
              )}
            </div>
          )}

          {/* Processing options */}
          <div>
            <h3 className="text-lg font-medium text-gray-900">Processing Options</h3>
            <div className="mt-4 space-y-4">
              <div>
                <label htmlFor="confidence" className="form-label">
                  Confidence Threshold: {confidence}
                </label>
                <input
                  type="range"
                  id="confidence"
                  name="confidence"
                  min="0.1"
                  max="0.9"
                  step="0.1"
                  value={confidence}
                  onChange={(e) => setConfidence(parseFloat(e.target.value))}
                  className="mt-1 w-full"
                />
                <p className="mt-1 text-sm text-gray-500">
                  Higher values are more selective (fewer false positives, might miss subtle nodules).
                  Lower values detect more potential nodules (more false positives).
                </p>
              </div>

              <div className="flex items-start">
                <div className="flex items-center h-5">
                  <input
                    id="lungs-only"
                    name="lungs-only"
                    type="checkbox"
                    checked={lungsOnly}
                    onChange={(e) => setLungsOnly(e.target.checked)}
                    className="focus:ring-indigo-500 h-4 w-4 text-indigo-600 border-gray-300 rounded"
                  />
                </div>
                <div className="ml-3 text-sm">
                  <label htmlFor="lungs-only" className="font-medium text-gray-700">
                    Lungs Segmentation Only
                  </label>
                  <p className="text-gray-500">
                    Only perform lung segmentation without nodule detection
                  </p>
                </div>
              </div>

              <div className="flex items-start">
                <div className="flex items-center h-5">
                  <input
                    id="no-segmentation"
                    name="no-segmentation"
                    type="checkbox"
                    checked={noSegmentation}
                    onChange={(e) => setNoSegmentation(e.target.checked)}
                    className="focus:ring-indigo-500 h-4 w-4 text-indigo-600 border-gray-300 rounded"
                  />
                </div>
                <div className="ml-3 text-sm">
                  <label htmlFor="no-segmentation" className="font-medium text-gray-700">
                    Skip Nodule Segmentation
                  </label>
                  <p className="text-gray-500">
                    Detect nodules but skip the detailed segmentation step
                  </p>
                </div>
              </div>
            </div>
          </div>

          {/* Error message */}
          {error && (
            <div className="rounded-md bg-red-50 p-4">
              <div className="flex">
                <ExclamationCircleIcon className="h-5 w-5 text-red-400" />
                <div className="ml-3">
                  <h3 className="text-sm font-medium text-red-800">{error}</h3>
                </div>
              </div>
            </div>
          )}

          {/* Upload progress */}
          {isUploading && (
            <div className="mt-4">
              <h4 className="text-sm font-medium text-gray-700">Uploading and processing...</h4>
              <div className="w-full bg-gray-200 rounded-full h-2.5 mt-2">
                <div 
                  className="bg-indigo-600 h-2.5 rounded-full transition-all duration-300"
                  style={{ width: `${progress}%` }}
                ></div>
              </div>
              <p className="text-xs text-gray-500 mt-1">
                {progress < 100 ? 'Uploading...' : 'Processing scan... This may take several minutes'}
              </p>
            </div>
          )}

          {/* Submit button */}
          <div className="flex justify-end">
            <button
              type="button"
              onClick={handleUpload}
              disabled={isUploading || files.length === 0}
              className={`btn-primary ${
                isUploading || files.length === 0 ? 'opacity-50 cursor-not-allowed' : ''
              }`}
            >
              {isUploading ? 'Processing...' : 'Upload and Process'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UploadPage;
