import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';
import { MoonIcon, SunIcon, PencilIcon, CheckIcon, XMarkIcon, ExclamationCircleIcon } from '@heroicons/react/24/outline';
import axios from 'axios';

const SettingsPage = () => {
  const { user, login } = useAuth();
  const { darkMode, toggleDarkMode } = useTheme();
  const [notifications, setNotifications] = useState(true);
  const [emailUpdates, setEmailUpdates] = useState(true);
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isEditMode, setIsEditMode] = useState(false);
  
  // Profile state
  const [profile, setProfile] = useState({
    username: user?.username || '',
    email: user?.email || '',
    currentPassword: '',
    newPassword: '',
    confirmPassword: '',
    firstName: user?.firstName || '',
    lastName: user?.lastName || ''
  });

  // Setup profile from user context when it changes
  useEffect(() => {
    if (user) {
      setProfile(prevProfile => ({
        ...prevProfile,
        username: user.username || '',
        email: user.email || '',
        firstName: user.firstName || '',
        lastName: user.lastName || ''
      }));
    }
  }, [user]);

  const resetForm = () => {
    setProfile({
      username: user?.username || '',
      email: user?.email || '',
      currentPassword: '',
      newPassword: '',
      confirmPassword: '',
      firstName: user?.firstName || '',
      lastName: user?.lastName || ''
    });
    setIsEditMode(false);
  };

  const handleProfileChange = (e) => {
    const { name, value } = e.target;
    setProfile(prevProfile => ({
      ...prevProfile,
      [name]: value
    }));
  };

  const validateForm = () => {
    // Check if passwords match if user is changing password
    if (profile.newPassword) {
      if (!profile.currentPassword) {
        setErrorMessage('Current password is required to set a new password');
        return false;
      }
      if (profile.newPassword !== profile.confirmPassword) {
        setErrorMessage('New passwords do not match');
        return false;
      }
      if (profile.newPassword.length < 6) {
        setErrorMessage('New password must be at least 6 characters');
        return false;
      }
    }

    // Validate username
    if (!profile.username || profile.username.length < 3) {
      setErrorMessage('Username must be at least 3 characters');
      return false;
    }

    // Validate email format
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (profile.email && !emailRegex.test(profile.email)) {
      setErrorMessage('Please enter a valid email address');
      return false;
    }

    return true;
  };

  const updateUserProfile = async () => {
    if (!validateForm()) {
      return;
    }

    setIsLoading(true);
    setErrorMessage('');
    
    try {
      // Create update payload
      const updateData = {
        username: profile.username,
        email: profile.email,
        first_name: profile.firstName,
        last_name: profile.lastName
      };

      // Only include password fields if user is changing password
      if (profile.newPassword) {
        updateData.current_password = profile.currentPassword;
        updateData.password = profile.newPassword;
      }

      // Call API to update user profile
      const response = await axios.put(`/api/users/${user.username}`, updateData);
      
      if (response.data.success) {
        setSuccessMessage('Profile updated successfully!');
        
        // Update local storage with new user data if username changed
        if (profile.username !== user.username) {
          // Re-login to get a new token with the updated username
          const loginResponse = await login(profile.username, profile.currentPassword || profile.newPassword);
          if (!loginResponse.success) {
            setErrorMessage('Profile updated but session refresh failed. Please login again.');
          }
        } else {
          // Update local storage manually for other fields
          const userData = JSON.parse(localStorage.getItem('user') || '{}');
          userData.email = profile.email;
          userData.firstName = profile.firstName;
          userData.lastName = profile.lastName;
          localStorage.setItem('user', JSON.stringify(userData));
        }
        
        setIsEditMode(false);
      }
    } catch (error) {
      console.error('Error updating profile:', error);
      
      // Handle error response and show appropriate message
      if (error.response?.status === 401) {
        setErrorMessage('Current password is incorrect. Please try again.');
      } else if (error.response?.status === 403) {
        setErrorMessage('Unauthorized. You do not have permission to update this profile.');
      } else {
        setErrorMessage(error.response?.data?.error || 'Failed to update profile. Please try again.');
      }
    } finally {
      setIsLoading(false);
    }
    
    // Clear the password fields
    setProfile(prev => ({
      ...prev,
      currentPassword: '',
      newPassword: '',
      confirmPassword: ''
    }));
    
    // Clear the success message after 3 seconds
    setTimeout(() => {
      setSuccessMessage('');
    }, 3000);
  };

  const saveSettings = () => {
    // In a real app, this would save settings to a backend
    setSuccessMessage('Settings saved successfully!');
    
    // Clear the success message after 3 seconds
    setTimeout(() => {
      setSuccessMessage('');
    }, 3000);
  };

  return (
    <div className={`max-w-4xl mx-auto ${darkMode ? 'text-white' : ''}`}>
      <div className="md:flex md:items-center md:justify-between mb-8">
        <div className="min-w-0 flex-1">
          <h2 className={`text-2xl font-bold leading-7 ${darkMode ? 'text-white' : 'text-gray-900'} sm:truncate sm:text-3xl sm:tracking-tight`}>
            Settings
          </h2>
          <p className={`mt-1 text-sm ${darkMode ? 'text-gray-300' : 'text-gray-500'}`}>
            Manage your account settings and preferences
          </p>
        </div>
      </div>

      {successMessage && (
        <div className="mb-6 bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded relative" role="alert">
          <span className="block sm:inline">{successMessage}</span>
        </div>
      )}

      {errorMessage && (
        <div className={`mb-6 rounded-md ${darkMode ? 'bg-red-900 border-red-700 text-red-100' : 'bg-red-50 border-red-200 text-red-700'} px-4 py-3 border relative`} role="alert">
          <div className="flex">
            <ExclamationCircleIcon className="h-5 w-5 text-red-400 mr-2" />
            <span className="block sm:inline">{errorMessage}</span>
          </div>
        </div>
      )}

      <div className={`${darkMode ? 'bg-gray-800 border-gray-700' : 'bg-white border-gray-200'} shadow overflow-hidden sm:rounded-lg mb-6`}>
        <div className="px-4 py-5 sm:px-6">
          <h3 className={`text-lg leading-6 font-medium ${darkMode ? 'text-white' : 'text-gray-900'}`}>
            Theme Settings
          </h3>
          <p className={`mt-1 max-w-2xl text-sm ${darkMode ? 'text-gray-300' : 'text-gray-500'}`}>
            Customize the appearance of the application
          </p>
        </div>
        <div className={`border-t ${darkMode ? 'border-gray-700' : 'border-gray-200'} px-4 py-5 sm:p-6`}>
          <div className="flex items-center justify-between">
            <div>
              <h4 className={`text-base font-medium ${darkMode ? 'text-white' : 'text-gray-900'}`}>Dark Mode</h4>
              <p className={`text-sm ${darkMode ? 'text-gray-300' : 'text-gray-500'}`}>
                Toggle between light and dark themes
              </p>
            </div>
            <button
              type="button"
              onClick={toggleDarkMode}
              className={`relative inline-flex flex-shrink-0 h-6 w-11 border-2 border-transparent rounded-full cursor-pointer transition-colors ease-in-out duration-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 ${
                darkMode ? 'bg-indigo-600' : 'bg-gray-200'
              }`}
              aria-pressed={darkMode}
            >
              <span className="sr-only">Toggle dark mode</span>
              <span
                className={`pointer-events-none inline-block h-5 w-5 rounded-full bg-white shadow transform ring-0 transition ease-in-out duration-200 ${
                  darkMode ? 'translate-x-5' : 'translate-x-0'
                }`}
              >
                {darkMode ? (
                  <MoonIcon className="h-3 w-3 text-indigo-600 m-1" />
                ) : (
                  <SunIcon className="h-3 w-3 text-yellow-500 m-1" />
                )}
              </span>
            </button>
          </div>
        </div>
      </div>

      <div className={`${darkMode ? 'bg-gray-800 border-gray-700' : 'bg-white border-gray-200'} shadow overflow-hidden sm:rounded-lg mb-6`}>
        <div className="px-4 py-5 sm:px-6 flex justify-between">
          <div>
            <h3 className={`text-lg leading-6 font-medium ${darkMode ? 'text-white' : 'text-gray-900'}`}>
              Account Information
            </h3>
            <p className={`mt-1 max-w-2xl text-sm ${darkMode ? 'text-gray-300' : 'text-gray-500'}`}>
              Your personal details and profile
            </p>
          </div>
          
          {/* Edit/Cancel buttons */}
          <div>
            {isEditMode ? (
              <div className="flex space-x-2">
                <button
                  type="button"
                  onClick={updateUserProfile}
                  disabled={isLoading}
                  className={`inline-flex items-center px-3 py-1.5 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 ${isLoading ? 'opacity-60 cursor-not-allowed' : ''}`}
                >
                  {isLoading ? 'Saving...' : 'Save'}
                  <CheckIcon className="ml-1.5 h-4 w-4" />
                </button>
                <button
                  type="button"
                  onClick={resetForm}
                  disabled={isLoading}
                  className={`inline-flex items-center px-3 py-1.5 border ${darkMode ? 'border-gray-600 text-gray-300 hover:bg-gray-700' : 'border-gray-300 text-gray-700 hover:bg-gray-50'} rounded-md text-sm font-medium focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500`}
                >
                  Cancel
                  <XMarkIcon className="ml-1.5 h-4 w-4" />
                </button>
              </div>
            ) : (
              <button
                type="button"
                onClick={() => setIsEditMode(true)}
                className={`inline-flex items-center px-3 py-1.5 border ${darkMode ? 'border-gray-600 text-gray-300 hover:bg-gray-700' : 'border-gray-300 text-gray-700 hover:bg-gray-50'} rounded-md text-sm font-medium focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500`}
              >
                Edit Profile
                <PencilIcon className="ml-1.5 h-4 w-4" />
              </button>
            )}
          </div>
        </div>
        
        {isEditMode ? (
          // Edit mode - show form
          <div className={`border-t ${darkMode ? 'border-gray-700' : 'border-gray-200'} px-4 py-5 sm:p-6`}>
            <div className="grid grid-cols-1 gap-y-6 gap-x-4 sm:grid-cols-6">
              <div className="sm:col-span-3">
                <label htmlFor="username" className={`block text-sm font-medium ${darkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                  Username
                </label>
                <div className="mt-1">
                  <input
                    type="text"
                    name="username"
                    id="username"
                    value={profile.username}
                    onChange={handleProfileChange}
                    className={`shadow-sm focus:ring-indigo-500 focus:border-indigo-500 block w-full sm:text-sm ${darkMode ? 'bg-gray-700 border-gray-600 text-white' : 'border-gray-300 text-gray-900'} rounded-md`}
                  />
                </div>
              </div>

              <div className="sm:col-span-3">
                <label htmlFor="email" className={`block text-sm font-medium ${darkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                  Email address
                </label>
                <div className="mt-1">
                  <input
                    type="email"
                    name="email"
                    id="email"
                    value={profile.email}
                    onChange={handleProfileChange}
                    className={`shadow-sm focus:ring-indigo-500 focus:border-indigo-500 block w-full sm:text-sm ${darkMode ? 'bg-gray-700 border-gray-600 text-white' : 'border-gray-300 text-gray-900'} rounded-md`}
                  />
                </div>
              </div>

              <div className="sm:col-span-3">
                <label htmlFor="firstName" className={`block text-sm font-medium ${darkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                  First name
                </label>
                <div className="mt-1">
                  <input
                    type="text"
                    name="firstName"
                    id="firstName"
                    value={profile.firstName}
                    onChange={handleProfileChange}
                    className={`shadow-sm focus:ring-indigo-500 focus:border-indigo-500 block w-full sm:text-sm ${darkMode ? 'bg-gray-700 border-gray-600 text-white' : 'border-gray-300 text-gray-900'} rounded-md`}
                  />
                </div>
              </div>

              <div className="sm:col-span-3">
                <label htmlFor="lastName" className={`block text-sm font-medium ${darkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                  Last name
                </label>
                <div className="mt-1">
                  <input
                    type="text"
                    name="lastName"
                    id="lastName"
                    value={profile.lastName}
                    onChange={handleProfileChange}
                    className={`shadow-sm focus:ring-indigo-500 focus:border-indigo-500 block w-full sm:text-sm ${darkMode ? 'bg-gray-700 border-gray-600 text-white' : 'border-gray-300 text-gray-900'} rounded-md`}
                  />
                </div>
              </div>
              
              <div className="sm:col-span-6">
                <h4 className={`text-sm font-medium ${darkMode ? 'text-gray-300' : 'text-gray-700'} mb-3`}>Change Password</h4>
                <p className={`text-xs ${darkMode ? 'text-gray-400' : 'text-gray-500'} mb-4`}>
                  Leave these fields blank if you don't want to change your password
                </p>
              </div>

              <div className="sm:col-span-6">
                <label htmlFor="currentPassword" className={`block text-sm font-medium ${darkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                  Current Password
                </label>
                <div className="mt-1">
                  <input
                    type="password"
                    name="currentPassword"
                    id="currentPassword"
                    value={profile.currentPassword}
                    onChange={handleProfileChange}
                    className={`shadow-sm focus:ring-indigo-500 focus:border-indigo-500 block w-full sm:text-sm ${darkMode ? 'bg-gray-700 border-gray-600 text-white' : 'border-gray-300 text-gray-900'} rounded-md`}
                  />
                </div>
              </div>

              <div className="sm:col-span-3">
                <label htmlFor="newPassword" className={`block text-sm font-medium ${darkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                  New Password
                </label>
                <div className="mt-1">
                  <input
                    type="password"
                    name="newPassword"
                    id="newPassword"
                    value={profile.newPassword}
                    onChange={handleProfileChange}
                    className={`shadow-sm focus:ring-indigo-500 focus:border-indigo-500 block w-full sm:text-sm ${darkMode ? 'bg-gray-700 border-gray-600 text-white' : 'border-gray-300 text-gray-900'} rounded-md`}
                  />
                </div>
              </div>

              <div className="sm:col-span-3">
                <label htmlFor="confirmPassword" className={`block text-sm font-medium ${darkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                  Confirm New Password
                </label>
                <div className="mt-1">
                  <input
                    type="password"
                    name="confirmPassword"
                    id="confirmPassword"
                    value={profile.confirmPassword}
                    onChange={handleProfileChange}
                    className={`shadow-sm focus:ring-indigo-500 focus:border-indigo-500 block w-full sm:text-sm ${darkMode ? 'bg-gray-700 border-gray-600 text-white' : 'border-gray-300 text-gray-900'} rounded-md`}
                  />
                </div>
              </div>
            </div>
          </div>
        ) : (
          // Read-only mode - show current information
          <div className={`border-t ${darkMode ? 'border-gray-700' : 'border-gray-200'}`}>
            <dl>
              <div className={`px-4 py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:px-6 ${darkMode ? 'bg-gray-800' : 'bg-gray-50'}`}>
                <dt className={`text-sm font-medium ${darkMode ? 'text-gray-300' : 'text-gray-500'}`}>
                  Username
                </dt>
                <dd className={`mt-1 text-sm ${darkMode ? 'text-white' : 'text-gray-900'} sm:mt-0 sm:col-span-2`}>
                  {user?.username || 'User'}
                </dd>
              </div>
              <div className={`px-4 py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:px-6 ${darkMode ? 'bg-gray-700' : 'bg-white'}`}>
                <dt className={`text-sm font-medium ${darkMode ? 'text-gray-300' : 'text-gray-500'}`}>
                  Role
                </dt>
                <dd className={`mt-1 text-sm capitalize ${darkMode ? 'text-white' : 'text-gray-900'} sm:mt-0 sm:col-span-2`}>
                  {user?.role || 'User'}
                </dd>
              </div>
              <div className={`px-4 py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:px-6 ${darkMode ? 'bg-gray-800' : 'bg-gray-50'}`}>
                <dt className={`text-sm font-medium ${darkMode ? 'text-gray-300' : 'text-gray-500'}`}>
                  Email address
                </dt>
                <dd className={`mt-1 text-sm ${darkMode ? 'text-white' : 'text-gray-900'} sm:mt-0 sm:col-span-2`}>
                  {user?.email || 'user@example.com'}
                </dd>
              </div>
              <div className={`px-4 py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:px-6 ${darkMode ? 'bg-gray-700' : 'bg-white'}`}>
                <dt className={`text-sm font-medium ${darkMode ? 'text-gray-300' : 'text-gray-500'}`}>
                  Full name
                </dt>
                <dd className={`mt-1 text-sm ${darkMode ? 'text-white' : 'text-gray-900'} sm:mt-0 sm:col-span-2`}>
                  {user?.firstName && user?.lastName 
                    ? `${user.firstName} ${user.lastName}`
                    : 'Not provided'
                  }
                </dd>
              </div>
            </dl>
          </div>
        )}
      </div>

      <div className={`${darkMode ? 'bg-gray-800 border-gray-700' : 'bg-white border-gray-200'} shadow overflow-hidden sm:rounded-lg mb-6`}>
        <div className="px-4 py-5 sm:px-6">
          <h3 className={`text-lg leading-6 font-medium ${darkMode ? 'text-white' : 'text-gray-900'}`}>
            Notification Settings
          </h3>
          <p className={`mt-1 max-w-2xl text-sm ${darkMode ? 'text-gray-300' : 'text-gray-500'}`}>
            Control how you receive notifications
          </p>
        </div>
        <div className={`border-t ${darkMode ? 'border-gray-700' : 'border-gray-200'} px-4 py-5 sm:p-6`}>
          <div className="space-y-6">
            <div className="flex items-start">
              <div className="flex items-center h-5">
                <input
                  id="notifications"
                  name="notifications"
                  type="checkbox"
                  checked={notifications}
                  onChange={() => setNotifications(!notifications)}
                  className="focus:ring-indigo-500 h-4 w-4 text-indigo-600 border-gray-300 rounded"
                />
              </div>
              <div className="ml-3 text-sm">
                <label htmlFor="notifications" className={`font-medium ${darkMode ? 'text-white' : 'text-gray-700'}`}>
                  In-app notifications
                </label>
                <p className={`${darkMode ? 'text-gray-300' : 'text-gray-500'}`}>
                  Receive notifications about your case status and results
                </p>
              </div>
            </div>
            <div className="flex items-start">
              <div className="flex items-center h-5">
                <input
                  id="email-updates"
                  name="email-updates"
                  type="checkbox"
                  checked={emailUpdates}
                  onChange={() => setEmailUpdates(!emailUpdates)}
                  className="focus:ring-indigo-500 h-4 w-4 text-indigo-600 border-gray-300 rounded"
                />
              </div>
              <div className="ml-3 text-sm">
                <label htmlFor="email-updates" className={`font-medium ${darkMode ? 'text-white' : 'text-gray-700'}`}>
                  Email updates
                </label>
                <p className={`${darkMode ? 'text-gray-300' : 'text-gray-500'}`}>
                  Receive email notifications about your case status and results
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="flex justify-end py-3">
        <button
          type="button"
          onClick={saveSettings}
          className="ml-3 inline-flex justify-center py-2 px-4 border border-transparent shadow-sm text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
        >
          Save notification settings
        </button>
      </div>
    </div>
  );
};

export default SettingsPage;
